import ModalService from '../services/modal.service';
import MyModal from './MyModal.vue';
export default {
  components: {
    MyModal
  },

  data() {
    return {
      modal: {}
    };
  },

  created() {
    ModalService.$on('open', ({
      component,
      props,
      resolve,
      reject
    }) => {
      this.modal = {
        component,
        props,
        close: value => {
          this.modal = {};
          resolve(value);
        },
        dismiss: reason => {
          this.modal = {};
          reject(reason);
        }
      };
    });
  }

};
